    public int shortestPath(int[][] grid, int k) {
        int result = shortestPath(grid, 0, 0, grid.length, grid[0].length, 0, 0, k);
        if (result == Integer.MAX_VALUE) {
            return -1;
        }
        return result;

    }

    private int shortestPath(int[][] grid, int i, int j, int R, int C, int obstacles, int distance, int k) {
        if (i < R && j < C) {
            if (grid[i][j] == 1) {
                obstacles++;
            }
            if (obstacles > k) {
                return Integer.MAX_VALUE;
            }
            if (i == R - 1 && j == C - 1 ) {
                return distance;
            }

            int first = shortestPath(grid, i + 1, j, R, C, obstacles, distance + 1, k);
            int second = shortestPath(grid, i, j + 1, R, C, obstacles, distance + 1, k);
            return Math.min(first, second);
        }
        return Integer.MAX_VALUE;
    }
